import React, { useContext, useEffect, useState } from 'react'
import userContext from '../../Context/userContext'
import { useRequestAxios, useFetch } from '../CustomHooks/getHooks'
function Exchange() {
  const { state, dispatch } = useContext(userContext)
  const { data, loading, error, refetch } = useRequestAxios(null)
  const [search, setSearch] = useState('')
  const options = {
    method: 'GET',
    url:
      'https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=100&page=1&sparkline=false',
  }
  useEffect(() => {
    refetch(options)
    return () => {}
  }, [])
  const handelChange = (e) => {
    setSearch(e.target.value)
  }
  const filterCoins = data
    ? data.filter((coin, i) =>
        coin.name.toLowerCase().includes(search.toLowerCase()),
      )
    : []

  const coins = filterCoins ? (
    filterCoins.map((coin, i) => {
      return (
        <tr key={i} style={{ backgroundColor: '#23252f' }}>
          <td>
            <img src={coin.image} alt="" className="image is-24x24" />
          </td>
          <td className="has-text-light">{coin.symbol}</td>
          <td className="has-text-info is-hidden-mobile">
            <a
              href={`https://coinmarketcap.com/currencies/${coin.name}`}
              target={'_blank'}
            >
              {coin.name}
            </a>
          </td>
          <td className="has-text-link is-hidden-mobile">
            {coin.current_price}
          </td>
          <td
            className={
              coin.price_change_percentage_24h < 0
                ? 'has-text-danger'
                : 'has-text-success'
            }
          >
            {coin.price_change_percentage_24h}
          </td>
        </tr>
      )
    })
  ) : (
    <tr></tr>
  )
  return (
    <article
      className="tile is-child box"
      style={{
        background: '#a8a8a8',
        // backgroundImage: 'url(https://images.unsplash.com/photo-1639261136395-4ef1623d2cb6?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80)',
        backgroundSize: 'cover',
      }}
    >
      <iframe
        name="TickerTape"
        src="https://darqube.com/external-embedding/ticker-tape?token=eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9..lxDLxZ9ZbWdEG4R-uChN_DQztCC2Dhwht_t-YS-spug"
        id="TickerTape"
        width={'100%'}
        height={50}
        scrolling="no"
      />

      <div className="container has-text-centered">
        <div className="column is-6 is-offset-3">
          <h1 className="title">Find Coins</h1>
          <h2 className="subtitle">$this is the best software platform .</h2>
          <div className="box">
            <div className="field is-grouped">
              <p className="control is-expanded">
                <input
                  className="input"
                  type="text"
                  placeholder="Enter "
                  onChange={handelChange}
                />
              </p>
              <p className="control">
                <a className="button is-info">Search</a>
              </p>
            </div>
          </div>
        </div>
      </div>

      <div className="container">
        <table className="table  is-striped is-fullwidth">
          <tbody>
            <tr className="th ">
              <th>Logo</th>
              <th>Code</th>
              <th className="is-hidden-mobile">Name</th>
              <th className="is-hidden-mobile">Price</th>
              <th>Evolution</th>
            </tr>
            {coins}
          </tbody>
        </table>
      </div>
    </article>
  )
}

export default Exchange
